mapper.update:
update employee set employeeId = #{employeeId},
,email = #{email},
,phone = #{phone}
,homeAddress = #{homeAddress}
where employeeid = #{employeeId}

mapper.select:
select employee.employeeId,employee.username,employee.name,employee.phone,employee.email,employee.position,employee.sex,employee.entryTime,department.departmentId,department.name as dname
from employee,department where employee.departmentname=department.name

    and employee.sex=#{sex}


    and employee.name like #{name}
