mapper.delete:
delete from Message where employeeId = #{employeeId};
delete from Meeting where employeeId = #{employeeId};
delete from EmployeeRole where employeeId = #{employeeId};
delete from receiveFile where sendfileId in (select sendfileId from sendfile where senderId = #{employeeId});
delete from sendFile where employeeId = #{employeeId};
delete from EmployeeTodo where employeeId = #{employeeId};
delete from CardHolder where ownerId = #{employeeId};
delete from card where ownerId = #{employeeId};
delete from EmployeeSchedule where employeeId = #{employeeId};
delete from employee where employeeId = #{employeeId};
delete from employee where employeeId = #{employeeId};

delete from EmployeeRole where roleId = #{roleId};
delete from RoleMenu where roleId = #{roleId};
delete from role where roleId = #{roleId};

mapper.update:
update employee set employeeId = #{employeeId},
,email = #{email},
,phone = #{phone}
,homeAddress = #{homeAddress}
where employeeid = #{employeeId}

mapper.select:
select employee.employeeId,employee.username,employee.name,employee.phone,employee.email,employee.position,employee.sex,employee.entryTime,department.departmentId,department.name as dname
from employee,department where employee.departmentname=department.name

    and employee.sex=#{sex}


    and employee.name like #{name}
