mapper.update:
update meetingroom set MeetingRoomId=#{MeetingRoomId}


        ,place=#{param2}


        ,isOccapy=#{param3}


        ,MeetingRoomName=#{MeetingRoomName}



where MeetingRoomId=#{MeetingRoomId}

mapper.select:
select meetingroom.meetingroomid,meetingroom.place,meetingroom.name,meetingroom.isOccapy,meetingroom.maxperson,meeting.name as meetingName,meeting.startTime,meeting.endTime
from meetingroom LEFT JOIN meeting on meetingroom.meetingRoomId=meeting.meetingRoomId and meeting.status=1 and CURRENT_DATE>startTime and endTime>CURRENT_DATE


        and meetingroom.place like #{place}


        and meetingroom.isOccapy=#{isOccapy}


        and meetingroom.name like CONCAT('%',CONCAT(#{MeetingRoomName},'%'))

select * from meetingroom


        name=#{param1}


        or place=#{param2}
